const rawData = $input.first().json.concatenated_content;

/**
 * Cleans and transforms the raw string data into an array of strings.
 * It splits the data by '--break--', trims whitespace from each chunk,
 * and filters out any resulting empty strings.
 */
const cleanedData = rawData
  .split('--break--')
  .map(chunk => chunk.trim())
  .filter(chunk => chunk.length > 0);

// Transform cleanedData to an array of items with {json: ...}
return cleanedData.map(data => ({ json: { data } }));

// This node takes the output from your Tavily search node.
const tavilyResponse = $json;
const searchResults = tavilyResponse.results || [];

const webDocuments = searchResults.map(result => {
  // Return an object with distinct source and content fields
  return {
    source: result.url,
    content: result.content
  };
});

// The output is a clean array of objects
return [{
  json: {
    web_documents: webDocuments
  }
}];

// --- Get data from the two input nodes ---
// IMPORTANT: Replace 'Internal_Docs_Processor' and 'Web_Search_Processor'
// with the actual names of the nodes in your n8n workflow.

const internalDocsNode = $('evaluator_decision').all();
const webDocsNode = $('web_results_clean').all();

// --- Extract the arrays from the node outputs ---

// This gets the array of text strings from your internal documents.
const internalTexts = internalDocsNode[0].json.relevant_texts || [];

// This gets the array of {source, content} objects from the web search.
const webDocuments = webDocsNode[0].json.web_documents || [];

// --- Format the internal documents into a structured array ---
const formattedInternalDocs = internalTexts.map((text, index) => {
  return {
    source: `Internal Document ${index + 1}`,
    content: text
  };
});

// --- Return the final merged JSON object ---
return [{
  json: {
    internal_documents: formattedInternalDocs,
    web_documents: webDocuments
  }
}];

// --- Step 1: Get data from the two input nodes ---
// IMPORTANT: Replace 'Reranker Output' and 'Evaluator Output' with the actual names
// of the nodes in your n8n workflow that provide these inputs.

const rerankerResults = $('Filter').all();
const evaluatorResults = $('Aggregate1').all();

// --- Step 2: Prepare the data for easy lookup ---

// The evaluator output is nested, so we extract the core array of evaluations.
const evaluations = evaluatorResults[0].json.data;

// Create a Map for quick access to reranked documents by their index.
// This is more efficient than searching the array in every loop.
const rerankedDocsMap = new Map();
for (const item of rerankerResults) {
  rerankedDocsMap.set(item.json.index, item.json.document.text);
}

// --- Step 3: Process the evaluations ---

let webSearchNeeded = false;
const relevantTexts = [];

// Loop through each evaluation result.
for (const evalItem of evaluations) {
  const isRelevant = evalItem.output.eval;
  const docIndex = evalItem.output.document_index;

  if (isRelevant) {
    // If the doc is relevant ('true'), find its text in our Map and add it.
    if (rerankedDocsMap.has(docIndex)) {
      relevantTexts.push(rerankedDocsMap.get(docIndex));
    }
  } else {
    // If any doc is not relevant ('false'), we signal that a web search is needed.
    webSearchNeeded = true;
  }
}

// --- Step 4: Return the final output ---

// Return the final object. The relevant texts are now in a JSON array.
return [{
  json: {
    webSearchNeeded: webSearchNeeded,
    relevant_texts: relevantTexts
  }
}];
